// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.17;

import { Pool } from '../Pool/Pool.sol';

contract PoolHarness is Pool {
    uint256 public constant ONE_HOUR = 1 hours;

  function canBeDefaulted() public view override returns (bool isAbleToDefault) {
    /// @dev Pool can be marked as defaulted only if it is not defaulted already and has lenders
    if (defaultedAt != 0 || _activeLendersCount == 0) {
      return false;
    }

    if (isBulletLoan) {
      /// @dev Pool can be marked as defaulted by lender only after (1 hours + maturity date) in case of bullet loan
      return block.timestamp > maturityDate + ONE_HOUR;
    }
    return super.canBeDefaulted();
  }
}

