pragma solidity 0.8.17;

import {IPool} from "./IPool.sol";
import {IPrime} from "../PrimeMembership/IPrime.sol";

/// @title Prime IPoolFactory interface
interface IPoolFactory {
    /// @notice Initialize the contract
    /// @dev This function is called only once during the contract deployment
    /// @param _prime Prime contract address
    /// @param _poolBeacon Beacon address for pool proxy pattern
    function __PoolFactory_init(address _prime, address _poolBeacon) external;

    /// @notice Creates a new pool
    /// @dev Callable only by prime members
    /// @param pooldata Bla bla bla
    /// @param members Pool members address encoded in bytes
    function createPool(
        IPool.PoolData calldata pooldata,
        bytes calldata members
    ) external;

    function prime() external view returns (IPrime);
}

contracts/Pool/Pool.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.17;

import {IPool} from "./IPool.sol";
import {IPoolFactory} from "./IPoolFactory.sol";
import {IPrime} from "../PrimeMembership/IPrime.sol";

import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";

import {IERC20Lite} from "./IERC20Lite.sol";
import {NZAGuard} from "../utils/NZAGuard.sol";
import {AddressCoder} from "../utils/AddressCoder.sol";

/// @title Pool contract is responsible for managing the pool
contract Pool is
    IPool,
    Initializable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable,
    NZAGuard
{
    /// @notice Standart year in seconds
    uint256 public constant YEAR = 360 days;

    /// @notice Pool publicity status
    bool public isPublic;

    /// @notice Pool availability status
    bool public isClosed;

    /// @notice Roll request status
    bool public isRollRequested;

    /// @notice Pool borrower address
    address public borrower;

    /// @notice Asset address of the pool
    address public asset;

    /// @notice Pool factory address
    IPoolFactory public factory;

    /// @notice Pool maximum size
    uint256 public maxSize;

    /// @notice Pool tenor
    uint256 public tenor;

    /// @notice Pool interest rate (in mantissa)
    uint256 public rateMantissa;

    /// @notice Protocol spread rate
    uint256 public spreadRate;

    /// @notice Origination fee rate
    uint256 public originationRate;

    /// @notice Pool rolling increment fee rate
    uint256 public incrementPerRoll;

    /// @notice Pool deposit window (in seconds)
    uint256 public depositWindow;

    /// @notice Pool current size
    uint256 public currentSize;

    /// @notice Pool deposit maturity
    uint256 public depositMaturity;

    /// @notice Pool maturity date
    uint256 public maturityDate;

    /// @notice Pool active roll id
    uint256 public activeRollId;

    /// @notice Pool lenders array
    address[] private _lenders;

    /// @notice Pool next roll id counter
    uint256 private _nextRollId;

    /// @notice Pool active lenders count
    uint256 private _activeLendersCount;

    /// @notice Pool active callbacks count
    uint256 private _activeCallbacksCount;

    /// @notice If pool is defaulted, this is the timestamp of the default
    uint256 public poolDefaultTimestamp;

    /// @notice Pool members mapping (lender address => Member struct)
    mapping(address => Member) private poolMembers;

    /// @notice Pool rolls mapping (roll id => Roll struct)
    mapping(uint256 => Roll) private _poolRolls;

    /// @notice Pool callbacks mapping (lender address => CallBack struct)
    mapping(address => CallBack) private _poolCallbacks;

    /// @notice Pool deposits mapping (lender address => deposit amount)
    mapping(address => uint256) private _poolDeposits;

    /// @notice Emitted when the pool is activated
    /// @param depositMaturity - Lender can deposit until this timestamp
    /// @param maturityDate - Borrower's maturity date (timestamp)
    event Activated(uint256 depositMaturity, uint256 maturityDate);

    /// @notice Emitted when the pool is closed
    event Closed();

    /// @notice Emitted when the pool is rolled
    /// @param rollId - Id of the new roll
    /// @param newMaturity - New maturity date (timestamp)
    event RollAccepted(uint256 indexed rollId, uint256 newMaturity);

    /// @notice Emitted when the roll is requested
    /// @param rollId - Id of the roll
    event RollRequested(uint256 indexed rollId);

    /// @notice Emitted when the roll is rejected
    /// @param rollId - Id of the roll
    /// @param user - Address of the user who rejected the roll
    event RollRejected(uint256 indexed rollId, address user);

    /// @notice Emitted when new lender is added to the pool
    event LenderWhitelisted(address lender);

    /// @notice Emitted when lender is removed from the pool
    event LenderBlacklisted(address lender);

    /// @notice Emitted when lender is rejected the roll to borrower
    event LenderRejectedRoll(uint256 indexed rollId, address lender);

    /// @notice Emitted when funds are lent to the pool
    event Lent(address indexed lender, uint256 amount);

    /// @notice Emitted when lender is repayed
    event Repayed(
        address indexed lender,
        uint256 repayed,
        uint256 spreadFee,
        uint256 originationFee
    );

    /// @notice Emitted when callback is created
    event CallbackCreated(address indexed lender);

    /// @notice Emitted when callback is cancelled
    event CallbackCancelled(address indexed lender);

    /// @notice Emitted when pool is converted to public
    event ConvertedToPublic();

    /// @notice Emitted when pool is defaulted
    event PoolDefaulted();

    /// @notice Modifier to check if the caller is a prime member
    modifier onlyPrime() {
        _isPrimeMember(msg.sender);
        _;
    }

    /// @notice Modifier to check if the caller is a pool borrower
    modifier onlyBorrower() {
        require(msg.sender == borrower, "NCR");
        _;
    }

    /// @notice Modifier to check if the pool is not closed
    modifier poolNotClosed() {
        require(!isClosed, "OAC");
        _;
    }
    /// @notice Modifier to check if the roll is requested
    modifier rollRequested() {
        require(isRollRequested, "ARM");
        _;
    }

    /// @notice Modifier to check if the pool is not defaulted
    modifier poolNotDefaulted() {
        require(poolDefaultTimestamp == 0, "PDD");
        _;
    }

    /// @inheritdoc IPool
    function __Pool_init(
        address _borrower,
        uint256 _spreadRate,
        uint256 _originationRate,
        uint256 _incrementPerRoll,
        PoolData calldata _poolData,
        bytes calldata _members
    ) external initializer {
        __Ownable_init();
        __ReentrancyGuard_init();
        __Pool_init_unchained(
            _borrower,
            _spreadRate,
            _originationRate,
            _incrementPerRoll,
            _poolData,
            _members
        );
    }

    /// @dev The __Pool_init_unchained sets initial parameters for the pool
    /// @param _borrower The address of the borrower that created the pool
    /// @param _spreadRate The rate at which protocol will earn spread
    /// @param _originationRate The rate of yield enhancement intended to incentivize collateral providers
    /// @param _incrementPerRoll - Pool rolling increment fee rate
    /// @param _poolData Data regarding the pool
    /// @param _members The list of members who rose the funds for the borrower
    function __Pool_init_unchained(
        address _borrower,
        uint256 _spreadRate,
        uint256 _originationRate,
        uint256 _incrementPerRoll,
        PoolData calldata _poolData,
        bytes calldata _members
    ) internal onlyInitializing {
        /// @dev Fill pool data
        borrower = _borrower;
        asset = _poolData.asset;
        maxSize = _poolData.size;
        tenor = _poolData.tenor;
        rateMantissa = _poolData.rateMantissa;
        depositWindow = _poolData.depositWindow;
        spreadRate = _spreadRate;
        originationRate = _originationRate;
        incrementPerRoll = _incrementPerRoll;

        /// @dev Starting new rolls from 1
        ++_nextRollId;

        /// @dev Factory is caller of initializer
        factory = IPoolFactory(msg.sender);

        /// @dev Pool is available for all prime users if it is public
        if (_members.length == 0) {
            isPublic = true;
        } else {
            _parseLenders(true, _members);
        }
    }

    /// @inheritdoc IPool
    function whitelistLenders(
        bytes calldata lenders
    ) external override onlyBorrower nonReentrant returns (bool success) {
        require(lenders.length != 0, "LLZ");

        /// @dev Pool converts to private if it is public
        if (isPublic) {
            isPublic = false;
        }
        _parseLenders(true, lenders);
        return true;
    }

    /// @inheritdoc IPool
    function blacklistLenders(
        bytes calldata lenders
    ) external override onlyBorrower nonReentrant returns (bool success) {
        require(lenders.length != 0, "LLZ");

        _parseLenders(false, lenders);
        return true;
    }

    /// @inheritdoc IPool
    function switchToPublic()
        external
        override
        onlyBorrower
        nonReentrant
        returns (bool success)
    {
        require(!isPublic, "AAD");

        isPublic = true;

        emit ConvertedToPublic();
        return true;
    }

    /// @inheritdoc IPool
    function lend(
        uint256 amount
    )
        external
        override
        nonReentrant
        onlyPrime
        nonZeroValue(amount)
        poolNotClosed
        poolNotDefaulted
        returns (bool success)
    {
        return _lend(amount, msg.sender);
    }

    /// @inheritdoc IPool
    function repay(
        address lender
    )
        external
        override
        onlyBorrower
        nonZeroAddress(lender)
        poolNotDefaulted
        nonReentrant
        returns (bool success)
    {
        return _repay(lender);
    }

    /// @inheritdoc IPool
    function repayAll()
        external
        override
        onlyBorrower
        poolNotDefaulted
        nonReentrant
        returns (bool success)
    {
        uint256 lendersLength = _lenders.length;
        for (uint256 i = 0; i < lendersLength; ++i) {
            _repay(_lenders[i]);
        }
        return true;
    }

    /// @inheritdoc IPool
    function requestCallBack()
        external
        override
        onlyPrime
        poolNotDefaulted
        poolNotClosed
        returns (bool success)
    {
        /// @dev Lender should have principal
        require(poolMembers[msg.sender].principal != 0, "LZL");

        /// @dev Lender should not have created callback
        require(!_poolCallbacks[msg.sender].isCreated, "AAD");

        /// @dev Callback can be created only before the maturity date
        require(maturityDate > block.timestamp, "EMD");

        /// @dev If last lender requests callback and roll is requested
        /// @dev then roll is rejected
        if (isRollRequested) {
            _rejectRoll();
        }

        /// @dev Increases the number of active callbacks
        _activeCallbacksCount++;

        /// @dev Saves callback as a struct
        _poolCallbacks[msg.sender] = CallBack(true, block.timestamp);

        emit CallbackCreated(msg.sender);
        return true;
    }

    /// @inheritdoc IPool
    function cancelCallBack()
        external
        override
        poolNotDefaulted
        onlyPrime
        returns (bool success)
    {
        /// @dev Lender should have created callback
        require(_poolCallbacks[msg.sender].isCreated, "AAD");

        /// @dev Removes callback
        delete _poolCallbacks[msg.sender];

        emit CallbackCancelled(msg.sender);
        return true;
    }

    /// @inheritdoc IPool
    function requestRoll()
        external
        override
        onlyBorrower
        poolNotDefaulted
        poolNotClosed
    {
        /// @dev Roll should be requested
        require(!isRollRequested, "RAR");

        /// @dev Roll can be requested only if there is one active lender and no active callbacks
        require(_activeLendersCount == 1, "RCR");

        /// @dev New roll can be activated only after deposit window until 48 hours before the maturity date
        require(
            block.timestamp > depositMaturity &&
                block.timestamp > _poolRolls[activeRollId].startDate &&
                block.timestamp < maturityDate - 48 hours,
            "RTR"
        );

        isRollRequested = true;

        emit RollRequested(_nextRollId);
    }

    function acceptRoll()
        external
        override
        onlyPrime
        rollRequested
        poolNotClosed
        poolNotDefaulted
    {
        /// @dev Lender can accept roll only before it starts
        require(block.timestamp < maturityDate, "RTR");

        /// @dev Should be an authorized lender
        require(poolMembers[msg.sender].principal != 0, "IMB");

        /// @dev Roll can be accepted only if there is one active lender
        require(_activeLendersCount == 1, "RCR");

        isRollRequested = false; // renew request status

        /// @dev Get the current roll id
        uint256 currentRollId = _nextRollId;

        /// @dev Increment the rolls counter
        ++_nextRollId;

        /// @dev Update the roll id tracker
        activeRollId = currentRollId;

        /// @dev Save the new roll as Roll struct
        _poolRolls[currentRollId] = Roll(maturityDate, maturityDate + tenor);

        /// @dev Prolongate the maturity date
        maturityDate += tenor;

        emit RollAccepted(currentRollId, maturityDate);
    }

    /// @inheritdoc IPool
    function markPoolDefaulted() external poolNotClosed poolNotDefaulted {
        /// @dev Governor is able to mark pool as defaulted through the Factory
        if (msg.sender != address(factory)) {
            /// @dev Lender or the borrower with loan can mark pool as defaulted
            _isPrimeMember(msg.sender);

            if (msg.sender != borrower) {
                /// @dev Lender should have principal
                require(poolMembers[msg.sender].principal != 0, "IMB");
            }

            /// @dev Pool can be marked as defaulted by lender only after (72 hours + maturity date)
            require(
                block.timestamp > maturityDate &&
                    block.timestamp - maturityDate > 72 hours,
                "EDR"
            );
        }

        /// @dev Set the pool default timestamp
        poolDefaultTimestamp = block.timestamp;

        emit PoolDefaulted();
    }

    /// @inheritdoc IPool
    function close()
        external
        override
        onlyBorrower
        poolNotClosed
        returns (bool success)
    {
        /// @dev The pool can be closed only if it's size is 0
        require(currentSize == 0, "OHD");
        _close();
        return true;
    }

    /// @inheritdoc IPool
    function totalDue()
        external
        view
        override
        returns (uint256 totalDueAmount)
    {
        /// @dev Gas optimization
        uint256 lendersLength = _lenders.length;
        for (uint256 i = 0; i < lendersLength; ++i) {
            /// @dev Second variable is spread amount, it is already included in lender's due
            (uint256 due, , uint256 originationFee) = dueOf(_lenders[i]);
            totalDueAmount += due + originationFee;
        }
    }

    /// @inheritdoc IPool
    function dueOf(
        address lender
    )
        public
        view
        override
        returns (uint256 due, uint256 spreadAmount, uint256 originationFee)
    {
        /// @dev Gas saving link to lender's member struct
        Member storage member = poolMembers[lender];

        /// @dev If principal is zero, due is zero too
        if (member.principal == 0) {
            return (0, 0, 0);
        }

        /// @dev Define the lender's active timestamp and origination fee if any
        uint256 principalPaymentTimestamp;
        (principalPaymentTimestamp, originationFee) = _getTimestampAndOrigFee(
            lender
        );

        uint256 principalPlusInterest = _principalPlusInterest(
            lender,
            principalPaymentTimestamp
        );
        /// @dev Due calculation. due == principal + interest + penalty + originationFee
        due = principalPlusInterest + _getPenaltyFee(lender, maturityDate);
        /// @dev Calculate spread amount
        /// @dev SpreadAmount = spreadRate * (loanAmount * annualBorrowAPR)
        /// @dev SpreadAmount = spreadRate * (principalPlusInterest - principal)
        /// @dev SpreadAmount = spreadRate * (interest)
        spreadAmount =
            (spreadRate * (principalPlusInterest - member.principal)) /
            1e18;
    }

    /// @inheritdoc IPool
    function balanceOf(address lender) public view override returns (uint256) {
        /// @dev If principal is zero, balance is zero too
        if (poolMembers[lender].principal == 0) {
            return 0;
        }

        return _principalPlusInterest(lender, block.timestamp);
    }

    /// @inheritdoc IPool
    function penaltyOf(address lender) public view override returns (uint256) {
        /// @dev In common case, penalty starts from maturity date
        return _getPenaltyFee(lender, maturityDate);
    }

    /// @notice Returns Prime address
    /// @dev Prime converted as IPrime interface
    /// @return primeInstance - Prime address
    function prime() public view returns (IPrime primeInstance) {
        /// @dev Factory should keep actual link to Prime
        return factory.prime();
    }

    /// @notice Parses the members encoded in bytes and calls _parseLender() for each member
    /// @dev Internal function
    /// @param isWhitelistOperation - True if the operation is a whitelist operation
    /// @param members - The encoded members bytes
    function _parseLenders(
        bool isWhitelistOperation,
        bytes calldata members
    ) internal {
        if (members.length == 20) {
            _parseLender(
                isWhitelistOperation,
                AddressCoder.decodeAddress(members)[0]
            );
        } else {
            address[] memory addresses = AddressCoder.decodeAddress(members);
            uint256 length = addresses.length;

            require(length <= 60, "EAL");

            for (uint256 i = 0; i < length; i++) {
                _parseLender(isWhitelistOperation, addresses[i]);
            }
        }
    }

    /// @notice Creates lender if not exists and updates the whitelist status
    /// @dev Internal function
    /// @param isWhitelistOperation - True if the operation is a whitelist operation
    /// @param member - The address of the lender
    function _parseLender(bool isWhitelistOperation, address member) internal {
        _isPrimeMember(member);

        /// @dev Gas saving link to lender's member struct
        Member storage memberStruct = poolMembers[member];

        /// @dev Whitelist Lender
        if (isWhitelistOperation) {
            /// @dev Creates member if not exists
            if (!memberStruct.isCreated) {
                _initLender(member, true);
            } else {
                /// @dev Whitelists member if it is not whitelisted
                memberStruct.isWhitelisted = true;
            }

            emit LenderWhitelisted(member);
        } else {
            /// @dev If we blacklist a lender, it should exist
            require(memberStruct.isCreated, "IMB");

            memberStruct.isWhitelisted = false;

            emit LenderBlacklisted(member);
        }
    }

    /// @dev Creates lender if not exists and updates the whitelist status
    /// @param member - The address of the lender
    /// @param isWhitelistOperation - True if the operation is a whitelist operation
    function _initLender(address member, bool isWhitelistOperation) internal {
        /// @dev Creates lender if not exists
        if (!poolMembers[member].isCreated) {
            /// @dev Borrower cannot be a lender
            require(borrower != member, "BLS");
            /// @dev Init struct for lender's data
            poolMembers[member] = Member(
                true,
                isWhitelistOperation,
                0,
                0,
                1e18
            );
            _lenders.push(member);
        }
    }

    /// @notice Lends funds to the pool
    /// @dev Internal function
    /// @param amount - Amount of funds to lend
    /// @param lender - Lender address
    /// @return success - True if the funds are lent
    function _lend(
        uint256 amount,
        address lender
    ) internal returns (bool success) {
        /// @dev New size of the pool shouldn't be greater than max allowed size
        require(currentSize + amount <= maxSize, "OSE");

        /// @dev Gas saving link to lender's member struct
        Member storage member = poolMembers[lender];

        /// @dev If roll is public, we should create it's data structure
        if (isPublic) {
            _initLender(lender, true);
        } else {
            /// @dev If roll is private, lender should be whitelisted
            require(member.isWhitelisted, "IMB");
        }

        /// @dev If depositMaturity is zero, it means that the pool is not activated yet
        if (depositMaturity == 0) {
            /// @dev Set depositMaturity and maturityDate
            depositMaturity = block.timestamp + depositWindow;
            maturityDate = block.timestamp + tenor;
            emit Activated(depositMaturity, maturityDate);
        } else {
            require(block.timestamp <= depositMaturity, "DWC");
        }
        /// @dev Increase pool size, lender's deposit and active lenders count
        currentSize += amount;
        _poolDeposits[lender] += amount;

        if (member.principal == 0) {
            ++_activeLendersCount;
        }

        /// @dev Update lender's member struct
        member.principal = balanceOf(lender) + amount;
        member.supplyIndex = _getSupplyIndex(lender, block.timestamp);
        member.accrualTs = block.timestamp;

        emit Lent(lender, amount);

        return IERC20Lite(asset).transferFrom(lender, borrower, amount);
    }

    /// @notice Repays the lender
    /// @dev Internal function
    /// @param lender - Lender address
    /// @return success - True if the lender is repaid
    function _repay(address lender) internal returns (bool success) {
        /// @dev Short circuit for non lenders
        if (poolMembers[lender].principal == 0) {
            return true;
        }
        /// @dev Member struct link
        Member storage member = poolMembers[lender];

        /// @dev Calculate the amount of funds to repay
        (
            uint256 memberDueAmount,
            uint256 spreadFee,
            uint256 originationFee
        ) = dueOf(lender);

        /// @dev Cleanup lender callbacks
        if (_poolCallbacks[lender].isCreated) {
            _poolCallbacks[lender].isCreated = false;
        }

        /// @dev Cleanup lender roll
        if (activeRollId != 0) {
            activeRollId = 0;
        }

        /// @dev Remove lender's deposit from the pool
        currentSize -= _poolDeposits[lender];

        /// @dev Cleanup deposited amount for lender
        delete _poolDeposits[lender];

        /// @dev Emit repay event before potential pool closure
        emit Repayed(lender, memberDueAmount, spreadFee, originationFee);

        /// @dev Cleanup member struct
        member.principal = 0;
        member.accrualTs = block.timestamp;
        --_activeLendersCount;

        /// @dev Close pool if it is empty and deposit window is over
        if (currentSize == 0 && depositMaturity <= block.timestamp) {
            _close();
        }

        uint256 totalFees = spreadFee + originationFee;

        /// @dev Treasury is always not zero address. Pay protocol fees if any
        if (totalFees != 0) {
            require(
                IERC20Lite(asset).transferFrom(
                    msg.sender,
                    prime().treasury(),
                    totalFees
                ),
                "TFL"
            );
        }
        return
            IERC20Lite(asset).transferFrom(
                msg.sender,
                lender,
                memberDueAmount - spreadFee
            );
    }

    /// @dev Rejects the roll
    function _rejectRoll() internal {
        isRollRequested = false;
        emit RollRejected(_nextRollId, msg.sender);
    }

    /// @dev Closes the pool
    function _close() internal {
        isClosed = true;
        emit Closed();
    }

    /// @notice Calculates index for supply interest
    /// @dev Internal function
    /// @param lender - The address of the lender
    /// @param timestamp - The timestamp for which the index is calculated
    function _getSupplyIndex(
        address lender,
        uint256 timestamp
    ) internal view returns (uint256 newSupplyIndex) {
        Member storage member = poolMembers[lender];

        /* Short-circuit accumulating 0 interest */
        if (member.accrualTs == timestamp || member.accrualTs == 0) {
            return member.supplyIndex;
        }

        if (timestamp > maturityDate) {
            timestamp = maturityDate;
        }

        uint256 timeDelta = timestamp - member.accrualTs;
        uint256 currentRateMantissa = _annualRate(rateMantissa, timeDelta);
        /// @dev newSupplyIndex = (rateMantissa + 1) * supplyIndex / divisor
        return
            (currentRateMantissa * member.supplyIndex) /
            1e18 +
            member.supplyIndex;
    }

    /// @dev Calculates principal + interest
    /// @param lender - The address of the lender
    /// @param timestamp - The timestamp for which the supply index is calculated
    function _principalPlusInterest(
        address lender,
        uint256 timestamp
    ) internal view returns (uint256 principalPlusInterest) {
        Member storage member = poolMembers[lender];
        uint256 supplyIndex = _getSupplyIndex(lender, timestamp);
        return (supplyIndex * member.principal) / member.supplyIndex;
    }

    /// @dev Calculates principal timestamp and origination fee
    /// @param lender - The address of the lender
    function _getTimestampAndOrigFee(
        address lender
    ) internal view returns (uint256 timestamp, uint256 originationFee) {
        Member storage member = poolMembers[lender];

        uint256 unusedTime;

        /// @dev Check if the specified lender has rejected the roll in progress. It decreases lender's credit score.
        if (_poolCallbacks[lender].isCreated) {
            /// @dev If callback is created, timestamp is calculated to the current time.
            timestamp = block.timestamp;
            /// @dev If first roll rejected, not all origination fee is used.
            if (_nextRollId == 1 && maturityDate > block.timestamp) {
                /// @dev Borrower should have a discount if lender has created callback.
                unusedTime = maturityDate - block.timestamp;
                /// @dev In other cases unused origination fee is zero.
            }
        } else {
            /// @dev If lender hasn't created callback, his due is calculated from the maturity date
            timestamp = maturityDate;
        }

        if (originationRate != 0) {
            /// @dev origination fee  == (principal * [annual origination rate of funds used])
            originationFee =
                (member.principal *
                    (_annualRate(originationRate, tenor - unusedTime))) /
                1e18;

            // rollingOriginationFeeAmount
            // == originationFeeAmount + (( incrementPerRoll * originationFeeAmount )*( timeElapsed/time ))
            if (_nextRollId != 1) {
                if (_poolCallbacks[lender].isCreated) {
                    /// @dev If Callback been requested, origination fee is calculated from the start of the roll
                    /// @dev [times of tenor passed from maturity date] == (daysPassed) / tenor
                    /// @dev Summ origination fee with rolling origination fee
                    originationFee +=
                        (
                            (originationFee *
                                incrementPerRoll *
                                (block.timestamp -
                                    _poolRolls[activeRollId].startDate))
                        ) /
                        (tenor * 1e18); // 1e18 is divisor from incrementPerRoll
                } else {
                    originationFee +=
                        (originationFee *
                            incrementPerRoll *
                            (_nextRollId - 1)) / // originationFeeAmount // incrementPerRoll
                        1e18; // rolls count
                }
            }
        }
    }

    /// @notice Calculates penalty fee for the lender
    /// @dev Internal function
    /// @param lender - The address of the lender
    /// @param startingDate - The timestamp from which the penalty fee is calculated
    function _getPenaltyFee(
        address lender,
        uint256 startingDate
    ) internal view returns (uint256 penaltyFee) {
        /// @dev If principal is zero, no penalty fee is charged
        if (poolMembers[lender].principal == 0) {
            return 0;
        }

        /// @dev In common case, penalty fee is calculated to the current time
        uint256 endingDate = block.timestamp;

        if (poolDefaultTimestamp != 0) {
            /// @dev If pool is defaulted, penalty fee is calculated to the default date
            endingDate = poolDefaultTimestamp;
        }

        /// @dev Calculate penalty fee only if pool is overdue or defaulted
        if (endingDate > startingDate) {
            uint256 penaltyRateMantissa = prime().penaltyRate(
                endingDate - startingDate
            );

            /// @dev If penalty rate is zero, no penalty fee is charged
            if (penaltyRateMantissa == 0) {
                return 0;
            }

            /// @dev Penalty fee == (penaltyRateForTime * principal)
            /// @dev function callable only if principal is not zero
            return (penaltyRateMantissa * poolMembers[lender].principal) / 1e18;
        } else {
            /// @dev Else return zero
            return 0;
        }
    }

    /// @notice Calculates the annual rate for a given interest rate and specific interval
    /// @param _rateMantissa The interest rate as a mantissa between [0, 1e18]
    /// @param _timeDelta The interval in seconds
    /// @return rate as a mantissa between [0, 1e18]
    function _annualRate(
        uint256 _rateMantissa,
        uint256 _timeDelta
    ) internal pure returns (uint256) {
        return (_rateMantissa * _timeDelta) / YEAR;
    }

    /// @dev Checks if the address is a prime member
    /// @param _member - The address of the member
    function _isPrimeMember(address _member) internal view {
        require(prime().isMember(_member), "NPM");
    }
}

contracts/Pool/PoolFactory.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.17;

import {IPoolFactory} from "./IPoolFactory.sol";
import {IPrime} from "../PrimeMembership/IPrime.sol";
import {Pool, IPool} from "./Pool.sol";

import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import {BeaconProxy} from "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";

import {NZAGuard} from "../utils/NZAGuard.sol";

/// @title Prime PoolFactory contract is responsible for creating new pools
contract PoolFactory is
    IPoolFactory,
    Initializable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable,
    NZAGuard
{
    /// @notice Prime contract address
    IPrime public prime;

    /// @notice Beacon address for pool proxy pattern
    address public poolBeacon;

    /// @notice Array of pool addresses
    address[] public pools;

    /// @notice Emitted when prime contract address is changed
    event PrimeContractChanged(address oldAddress, address newAddress);

    /// @notice Emitted when pool beacon address is changed
    event PoolBeaconChanged(address oldAddress, address newAddress);

    /// @notice Emitted when a new pool is created
    event PoolCreated(
        address pool,
        address indexed borrower,
        address indexed asset,
        uint256 size,
        uint256 rateMantissa,
        uint256 tenor,
        uint256 depositWindow,
        uint256 spreadRate,
        uint256 originationRate
    );

    /// @notice Modifier to check if the caller is a prime member
    modifier onlyPrime() {
        _isPrimeMember(msg.sender);
        _;
    }

    /// @inheritdoc IPoolFactory
    function __PoolFactory_init(
        address _prime,
        address _poolBeacon
    )
        external
        override
        nonZeroAddress(_prime)
        nonZeroAddress(_poolBeacon)
        initializer
    {
        __Ownable_init();
        __ReentrancyGuard_init();
        prime = IPrime(_prime);
        poolBeacon = _poolBeacon;
    }

    /// @inheritdoc IPoolFactory
    function createPool(
        IPool.PoolData calldata pooldata,
        bytes calldata members
    )
        external
        override
        onlyPrime
        nonZeroAddress(pooldata.asset)
        nonZeroValue(pooldata.size)
    {
        /// @dev Tenor should be greater than 48 hours and greater than deposit window
        require(pooldata.depositWindow > 1 hours, "UTR");
        require(pooldata.tenor > pooldata.depositWindow + 48 hours, "DET");
        require(prime.isAssetAvailable(pooldata.asset), "AAI");

        /// @dev Fetches spread, origination rate and rolling increment from prime contract
        uint256 spreadRate = prime.spreadRate();
        uint256 originationRate = prime.originationRate();
        uint256 incrementPerRoll = prime.incrementPerRoll();

        /// @dev Creates a pool using beacon proxy pattern
        address pool = address(new BeaconProxy(poolBeacon, ""));

        /// @dev Initializes the pool according to the pool beacon pattern
        IPool(pool).__Pool_init(
            msg.sender,
            spreadRate,
            originationRate,
            incrementPerRoll,
            pooldata,
            members
        );

        pools.push(pool);

        emit PoolCreated(
            pool,
            msg.sender,
            pooldata.asset,
            pooldata.size,
            pooldata.rateMantissa,
            pooldata.tenor,
            pooldata.depositWindow,
            spreadRate,
            originationRate
        );
    }

    /// @notice Marks the pools as defaulted
    /// @dev Callable only by owner
    function defaultPools(address[] calldata _pools) external onlyOwner {
        uint256 length = _pools.length;
        for (uint256 i = 0; i < length; ++i) {
            IPool(_pools[i]).markPoolDefaulted();
        }
    }

    /// @notice Returns the pools array
    /// @return Array of pool addresses
    function getPools() external view returns (address[] memory) {
        return pools;
    }

    /// @notice Changes the prime contract address
    /// @dev Callable only by owner
    /// @param newAddress New prime contract address
    function setPrimeContract(
        address newAddress
    )
        external
        nonZeroAddress(newAddress)
        nonSameAddress(newAddress, address(prime))
        onlyOwner
    {
        address currentAddress = address(prime);

        prime = IPrime(newAddress);
        emit PrimeContractChanged(currentAddress, newAddress);
    }

    /// @notice Changes the pool beacon address
    /// @dev Callable only by owner
    /// @param _newPoolBeacon New pool beacon address
    function setPoolBeacon(
        address _newPoolBeacon
    )
        external
        nonZeroAddress(_newPoolBeacon)
        nonSameAddress(_newPoolBeacon, poolBeacon)
        onlyOwner
    {
        address currentAddress = poolBeacon;
        poolBeacon = _newPoolBeacon;

        emit PoolBeaconChanged(currentAddress, _newPoolBeacon);
    }

    /// @notice Checks if the caller is a prime member
    /// @dev Internal function, reverts if the caller is not a prime member
    /// @param _member Member address
    function _isPrimeMember(address _member) internal view {
        require(prime.isMember(_member), "NPM");
    }
}

contracts/PrimeMembership/Asset.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.17;

library Asset {
    struct Data {
        mapping(address => bool) flags;
        mapping(address => uint256) addressIndex;
        address[] addresses;
        uint256 id;
    }

    function insert(Data storage self, address asset) internal returns (bool) {
        if (self.flags[asset]) {
            return false;
        }

        self.flags[asset] = true;
        self.addresses.push(asset);
        self.addressIndex[asset] = self.id;
        self.id++;
        return true;
    }

    function contains(
        Data storage self,
        address asset
    ) internal view returns (bool) {
        return self.flags[asset];
    }

    function getList(
        Data storage self
    ) internal view returns (address[] memory) {
        return self.addresses;
    }
}

contracts/PrimeMembership/IPrime.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.17;

/**
 * @title Interface of the Prime membership contract
 */
interface IPrime {
    /// @notice Member status enum
    enum MemberStatus {
        PENDING,
        WHITELISTED,
        BLACKLISTED
    }

    /// @notice A record of member info
    struct Member {
        uint256 riskScore;
        MemberStatus status;
        bool created;
    }

    /**
     * @notice Check membership status for a given `_member`
     * @param _member The address of member
     * @return Boolean flag containing membership status
     */
    function isMember(address _member) external view returns (bool);

    /**
     * @notice Calculates the penalty rate for a given interval
     * @param interval The interval in seconds
     * @return The penalty rate as a mantissa between [0, 1e18]
     */
    function penaltyRate(uint256 interval) external view returns (uint256);

    /**
     * @notice Check Stablecoin existence for a given `asset` address
     * @param asset The address of asset
     * @return Boolean flag containing asset availability
     */
    function isAssetAvailable(address asset) external view returns (bool);

    /**
     * @notice Get membership info for a given `_member`
     * @param _member The address of member
     * @return The member info struct
     */
    function membershipOf(
        address _member
    ) external view returns (Member memory);

    /**
     * @notice Returns current protocol rate value
     * @return The protocol rate as a mantissa between [0, 1e18]
     */
    function spreadRate() external view returns (uint256);

    /**
     * @notice Returns current originated fee value
     * @return originated fee rate as a mantissa between [0, 1e18]
     */
    function originationRate() external view returns (uint256);

    /**
     * @notice Returns current rolling increment fee
     * @return rolling fee rate as a mantissa between [0, 1e18]
     */
    function incrementPerRoll() external view returns (uint256);

    /**
     * @notice Returns current protocol fee collector address
     * @return address of protocol fee collector
     */
    function treasury() external view returns (address);
}

contracts/PrimeMembership/Prime.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.17;

import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

import {IPrime} from "./IPrime.sol";
import {Asset} from "./Asset.sol";

import {NZAGuard} from "../utils/NZAGuard.sol";

/// @title A contract for control Clearpool Prime membership database
contract Prime is Initializable, OwnableUpgradeable, IPrime, NZAGuard {
    using Asset for Asset.Data;

    /// @notice Standart year in seconds
    uint256 public constant YEAR = 360 days;

    /// @notice Setted penalty rate per year value
    uint256 public penaltyRatePerYear;

    /// @dev Protocol spread rate
    uint256 public spreadRate; // from 0 (0%) to 1e18 (100%)

    /// @notice Origination fee rate
    uint256 public originationRate;

    /// @notice Rolling increment rate for the origination fee
    uint256 public incrementPerRoll;

    /// @dev The address that will receive the fees
    address public treasury;

    /// @dev Data struct to simplify the operations with stablecoins addresses
    Asset.Data private _stablecoins;

    /// @dev A record of each member's info, by address
    mapping(address => Member) private _members;

    /// @notice An event that's emitted when a member is created
    event MemberCreated(address indexed member);
    /// @notice An event that's emitted when a member is whitelisted
    event MemberWhitelisted(address indexed member);
    /// @notice An event that's emitted when a member is blacklisted
    event MemberBlacklisted(address indexed member);

    /// @notice An event that's emitted when a member's riskScore is changed
    event RiskScoreChanged(address indexed member, uint256 score);

    /// @notice An event that's emitted when the value of the blocksPerDay is changed
    event BlocksPerDayUpdated(uint256 oldValue, uint256 newValue);

    /// @notice An event that's emitted when the value of the penaltyRatePerYear is changed
    event PenaltyRatePerDayUpdated(uint256 oldValue, uint256 newValue);

    /// @notice An event that's emitted when the value of the spreadRate is changed
    event SpreadRateChanged(uint256 oldValue, uint256 newValue);

    /// @notice An event that's emitted when the value of the treasury is changed
    event TreasuryChanged(address oldValue, address newValue);

    /// @notice Emitted when origination fee rate is changed
    event OriginationRateChanged(uint256 oldFee, uint256 newFee);

    /// @notice Emitted when rolling increment rate is changed
    event RollingIncrementChanged(uint256 oldIncrement, uint256 newIncrement);

    /// @dev Modifier for checking membership record availability
    modifier onlyMember(address _member) {
        require(_members[_member].created, "NPM");
        _;
    }

    /// @dev Modifier for checking that risk score is in range of [1, 100]
    modifier riskScoreInRange(uint256 _riskScore) {
        require(_riskScore <= 100 && _riskScore > 0, "RSI");
        _;
    }

    /// @dev Internal function to initialize the contract after it's been added to the proxy.
    /// @dev It initializes the inherited contracts.
    /// @param stablecoins An array of stablecoins addresses
    /// @param treasury_ The address that will receive the fees
    /// @param penaltyRatePerYear_ The penalty rate per year
    function __Prime_init(
        address[] memory stablecoins,
        address treasury_,
        uint256 penaltyRatePerYear_
    ) external virtual initializer {
        __Ownable_init_unchained();
        __Prime_init_unchained(stablecoins, treasury_, penaltyRatePerYear_);
    }

    /// @dev Internal function to initialize the contract after it's been added to the proxy
    /// @dev It initializes current contract with the given parameters.
    /// @param stablecoins An array of stablecoins addresses
    /// @param treasury_ The address that will receive the fees
    /// @param penaltyRatePerYear_ The penalty rate per year
    function __Prime_init_unchained(
        address[] memory stablecoins,
        address treasury_,
        uint256 penaltyRatePerYear_
    )
        internal
        nonZeroAddress(treasury_)
        nonZeroValue(penaltyRatePerYear_)
        initializer
    {
        treasury = treasury_;
        penaltyRatePerYear = penaltyRatePerYear_;

        for (uint256 i = 0; i < stablecoins.length; i++) {
            require(_stablecoins.insert(stablecoins[i]), "TIF");
        }
    }

    /**
     * @inheritdoc IPrime
     */
    function isMember(address _member) external view override returns (bool) {
        Member storage member = _members[_member];
        return member.created && member.status == MemberStatus.WHITELISTED;
        // TODO: implement check for riskScore
    }

    /**
     * @inheritdoc IPrime
     */
    function penaltyRate(
        uint256 interval
    ) external view override returns (uint256 rate) {
        return (penaltyRatePerYear * interval) / YEAR;
    }

    /**
     * @inheritdoc IPrime
     */
    function isAssetAvailable(
        address asset
    ) external view override nonZeroAddress(asset) returns (bool isAvailable) {
        return _stablecoins.contains(asset);
    }

    /// @notice Returns an array of assets available for borrowing
    /// @return An array of available assets
    function availableAssets() external view returns (address[] memory) {
        return _stablecoins.getList();
    }

    /**
     * @inheritdoc IPrime
     */
    function membershipOf(
        address _member
    ) external view override returns (Member memory member) {
        return _members[_member];
    }

    /**
     * @notice Request a membership record
     *
     *
     * @dev Emits a {MemberCreated} event.
     */
    function requestMembership(
        address _requester
    ) public nonZeroAddress(_requester) {
        require(!_members[_requester].created, "MAC");

        _members[_requester] = Member(0, MemberStatus.PENDING, true);
        emit MemberCreated(_requester);
    }

    /**
     * @dev SHOULD BE DEPLOYED ON TESTNET ONLY
     */
    function whitelistMemberTestnet(
        address _member
    ) external nonZeroAddress(_member) {
        _whitelistMember(_member, 1);
    }

    /**
     * @notice Alter or creates membership record by setting `_member` status and `_riskScore`
     * @param _member The member address
     * @param _riskScore The number up to 100 representing member's score
     *
     * @dev Emits a {MemberCreated} event.
     * @dev Emits a {MemberWhitelisted} event.
     * @dev Emits a {RiskScoreChanged} event.
     */
    function whitelistMember(
        address _member,
        uint256 _riskScore
    ) external nonZeroAddress(_member) riskScoreInRange(_riskScore) onlyOwner {
        _whitelistMember(_member, _riskScore);
    }

    /// @dev Internal function that whitelists member
    /// @param _member The member address
    /// @param _riskScore The number up to 100 representing member's score
    function _whitelistMember(address _member, uint256 _riskScore) internal {
        Member storage member = _members[_member];

        if (!member.created) {
            requestMembership(_member);
        }

        require(member.status != MemberStatus.WHITELISTED, "AAD");

        member.status = MemberStatus.WHITELISTED;
        emit MemberWhitelisted(_member);

        if (member.riskScore != _riskScore) {
            member.riskScore = _riskScore;
            emit RiskScoreChanged(_member, _riskScore);
        }
    }

    /**
     * @notice Alter membership record by setting `_member` status
     * @param _member The member address
     *
     * @dev Emits a {MemberBlacklisted} event.
     */
    function blacklistMember(
        address _member
    ) external nonZeroAddress(_member) onlyMember(_member) onlyOwner {
        Member storage member = _members[_member];

        require(member.status != MemberStatus.BLACKLISTED, "AAD");

        member.status = MemberStatus.BLACKLISTED;
        emit MemberBlacklisted(_member);
    }

    /**
     * @notice Alter membership record by setting member `_riskScore`
     * @param _member The member address
     * @param _riskScore The number up to 100 representing member's score
     *
     * @dev Emits a {RiskScoreChanged} event.
     */
    function changeMemberRiskScore(
        address _member,
        uint256 _riskScore
    )
        external
        nonZeroAddress(_member)
        onlyMember(_member)
        riskScoreInRange(_riskScore)
        onlyOwner
    {
        Member storage member = _members[_member];
        if (member.riskScore != _riskScore) {
            member.riskScore = _riskScore;
            emit RiskScoreChanged(_member, _riskScore);
        }
    }

    /**
     * @notice Changes the spread rate
     * @dev Callable only by owner. It is a mantissa value, so 1e18 is 100%
     * @param spreadRate_ New fee collector address
     */
    function changeSpreadRate(
        uint256 spreadRate_
    )
        external
        onlyOwner
        nonMoreThenOne(spreadRate_)
        nonSameValue(spreadRate_, spreadRate)
    {
        uint256 currentValue = spreadRate;
        spreadRate = spreadRate_;
        emit SpreadRateChanged(currentValue, spreadRate_);
    }

    /// @notice Changes the origination fee rate
    /// @dev Callable only by owner
    /// @param _originationRate New origination fee rate
    function setOriginationRate(
        uint256 _originationRate
    )
        external
        onlyOwner
        nonMoreThenOne(_originationRate)
        nonSameValue(_originationRate, originationRate)
    {
        uint256 currentFee = originationRate;

        originationRate = _originationRate;
        emit OriginationRateChanged(currentFee, _originationRate);
    }

    /// @notice Changes the rolling increment fee rate
    /// @dev Callable only by owner
    /// @param _incrementPerRoll New origination fee rate
    function setRollingIncrement(
        uint256 _incrementPerRoll
    )
        external
        onlyOwner
        nonMoreThenOne(_incrementPerRoll)
        nonSameValue(_incrementPerRoll, incrementPerRoll)
    {
        uint256 currentIncrement = incrementPerRoll;

        incrementPerRoll = _incrementPerRoll;
        emit OriginationRateChanged(currentIncrement, _incrementPerRoll);
    }

    /// @notice Sets a new treasury address for the contract
    /// @dev Callable only by owner
    /// @param treasury_ The address of the new treasury
    function setTreasury(
        address treasury_
    )
        external
        nonZeroAddress(treasury_)
        nonSameAddress(treasury_, treasury)
        onlyOwner
    {
        address currentValue = treasury;

        treasury = treasury_;
        emit TreasuryChanged(currentValue, treasury_);
    }

    /// @notice Updates penalty rate per year value
    /// @dev Callable only by owner
    /// @param penaltyRatePerYear_ New penalty rate per year value
    function updatePenaltyRatePerYear(
        uint256 penaltyRatePerYear_
    )
        external
        onlyOwner
        nonZeroValue(penaltyRatePerYear_)
        nonSameValue(penaltyRatePerYear_, penaltyRatePerYear)
    {
        uint256 currentValue = penaltyRatePerYear;

        penaltyRatePerYear = penaltyRatePerYear_;
        emit PenaltyRatePerDayUpdated(currentValue, penaltyRatePerYear_);
    }
}

contracts/utils/AddressCoder.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.17;
pragma abicoder v2;

library AddressCoder {
    function encodeAddress(
        address[] calldata addresses
    ) internal pure returns (bytes memory data) {
        for (uint256 i = 0; i < addresses.length; i++) {
            data = abi.encodePacked(data, addresses[i]);
        }
    }

    function decodeAddress(
        bytes calldata data
    ) internal pure returns (address[] memory addresses) {
        uint256 n = data.length / 20;
        addresses = new address[](n);

        for (uint256 i = 0; i < n; i++) {
            addresses[i] = bytesToAddress(data[i * 20:(i + 1) * 20]);
        }
    }

    function bytesToAddress(
        bytes calldata data
    ) private pure returns (address addr) {
        bytes memory b = data;
        assembly {
            addr := mload(add(b, 20))
        }
    }
}

